import React from 'react';
import { Mic, MicOff, Settings, RotateCcw, Download } from 'lucide-react';

const ControlButtons = ({ 
  isRecording, 
  toggleRecording, 
  openSettings, 
  resetTranscript, 
  exportData 
}) => {
  return (
    <div className="flex flex-wrap gap-3 mb-6">
      <button
        onClick={toggleRecording}
        className={`control-btn ${isRecording ? 'bg-red-500 hover:bg-red-600' : 'bg-gradient-to-r from-orange-400 to-pink-400 hover:from-orange-500 hover:to-pink-500'}`}
      >
        {isRecording ? <MicOff className="h-4 w-4" /> : <Mic className="h-4 w-4" />}
        {isRecording ? 'Stop Recording' : 'Start Recording'}
      </button>
      <button
        onClick={openSettings}
        className="control-btn bg-gradient-to-r from-blue-400 to-purple-400 hover:from-blue-500 hover:to-purple-500"
      >
        <Settings className="h-4 w-4" />
        Settings
      </button>
      <button
        onClick={resetTranscript}
        className="control-btn bg-gradient-to-r from-pink-400 to-purple-400 hover:from-pink-500 hover:to-purple-500"
      >
        <RotateCcw className="h-4 w-4" />
        Reset
      </button>
      <button
        onClick={exportData}
        className="control-btn bg-gradient-to-r from-teal-400 to-blue-400 hover:from-teal-500 hover:to-blue-500"
      >
        <Download className="h-4 w-4" />
        Export
      </button>
    </div>
  );
};

export default ControlButtons;
