import React from 'react';

const Dashboard = ({ wordCount, speakerCount, avgWords, sessionDuration }) => {
  return (
    <div className="dashboard">
      <div className="dashboard-card">
        <div className="dashboard-icon">üìù</div>
        <div className="dashboard-label">Total Words</div>
        <div className="dashboard-value">{wordCount || 0}</div>
      </div>
      <div className="dashboard-card">
        <div className="dashboard-icon">üé§</div>
        <div className="dashboard-label">Unique Speakers</div>
        <div className="dashboard-value">{speakerCount || 0}</div>
      </div>
      <div className="dashboard-card">
        <div className="dashboard-icon">üìä</div>
        <div className="dashboard-label">Avg Words/Speaker</div>
        <div className="dashboard-value">{avgWords ? avgWords.toFixed(0) : 0}</div>
      </div>
      <div className="dashboard-card">
        <div className="dashboard-icon">‚è±Ô∏è</div>
        <div className="dashboard-label">Session Duration</div>
        <div className="dashboard-value">{sessionDuration}</div>
      </div>
    </div>
  );
};

export default Dashboard;
