import React from 'react';

const ControlButtons = ({ 
  isRecording, 
  isLoading, 
  onStartRecording, 
  onStopRecording, 
  onReset, 
  onExport, 
  onSettingsOpen 
}) => {
  
  const handleRecordingToggle = () => {
    if (isRecording) {
      onStopRecording();
    } else {
      onStartRecording();
    }
  };

  return (
    <div className="control-buttons">
      <button
        onClick={handleRecordingToggle}
        disabled={isLoading}
        className={`btn-primary ${isRecording ? 'recording' : ''} ${isLoading ? 'loading' : ''}`}
      >
        <span className="btn-icon">
          {isRecording ? 'üõë' : 'üé§'}
        </span>
        {isLoading ? 'Processing...' : isRecording ? 'Stop Recording' : 'Start Recording'}
      </button>
      
      <button
        onClick={onSettingsOpen}
        className="btn-secondary"
        disabled={isLoading}
      >
        <span className="btn-icon">‚öôÔ∏è</span>
        Settings
      </button>
      
      <button
        onClick={onReset}
        className="btn-secondary"
        disabled={isLoading}
      >
        <span className="btn-icon">üîÑ</span>
        Reset
      </button>
      
      <button
        onClick={onExport}
        className="btn-secondary"
        disabled={isLoading}
      >
        <span className="btn-icon">üíæ</span>
        Export
      </button>
    </div>
  );
};

export default ControlButtons;
