import React, { useState, useEffect } from 'react';
import Dashboard from './components/Dashboard';
import TranscriptCard from './components/TranscriptCard';
import SettingsModal from './components/SettingsModal';
import ControlButtons from './components/ControlButtons';
import useAudioRecording from './hooks/useAudioRecording';
import { translate } from './utils/translator';
import './App.css';

const App = () => {
  const [transcript, setTranscript] = useState([]);
  const [translatedTranscript, setTranslatedTranscript] = useState([]);
  const [language, setLanguage] = useState('en');
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [sessionStart, setSessionStart] = useState(null);
  const [sessionDuration, setSessionDuration] = useState('00:00');
  const [selectedOriginalLang, setSelectedOriginalLang] = useState('english');
  const [selectedTranslatedLang, setSelectedTranslatedLang] = useState('spanish');
  
  const {
    isRecording,
    isLoading,
    startRecording,
    stopRecording,
    resetRecording
  } = useAudioRecording();

  // Update current time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
      if (sessionStart) {
        const elapsed = Math.floor((Date.now() - sessionStart) / 1000);
        const minutes = Math.floor(elapsed / 60);
        const seconds = elapsed % 60;
        setSessionDuration(`${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);
      }
    }, 1000);
    return () => clearInterval(timer);
  }, [sessionStart]);

  // Handle recording state changes
  useEffect(() => {
    if (isRecording && !sessionStart) {
      setSessionStart(Date.now());
    } else if (!isRecording && sessionStart) {
      // Process the recording result here
      simulateTranscription();
    }
  }, [isRecording, sessionStart]);

  const simulateTranscription = () => {
    // Simulate transcription process
    const fakeData = [
      { speaker: 'Speaker 1', time: new Date().toLocaleTimeString(), text: 'Welcome to our quarterly review meeting.' },
      { speaker: 'Speaker 2', time: new Date().toLocaleTimeString(), text: 'Thank you for joining us today.' },
      { speaker: 'Speaker 3', time: new Date().toLocaleTimeString(), text: 'The results look very promising.' },
    ];
    
    setTranscript(prev => [...prev, ...fakeData]);
    setTranslatedTranscript(prev => [
      ...prev, 
      ...fakeData.map(line => ({ 
        ...line, 
        text: translate(line.text, language) 
      }))
    ]);
  };

  const handleStartRecording = () => {
    startRecording();
  };

  const handleStopRecording = () => {
    stopRecording();
  };

  const handleReset = () => {
    resetRecording();
    setTranscript([]);
    setTranslatedTranscript([]);
    setSessionStart(null);
    setSessionDuration('00:00');
  };

  const handleExport = () => {
    const dataToExport = {
      originalTranscript: transcript,
      translatedTranscript: translatedTranscript,
      sessionDuration: sessionDuration,
      exportTime: new Date().toISOString()
    };
    
    const blob = new Blob([JSON.stringify(dataToExport, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `meeting-transcript-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleSettingsOpen = () => {
    setIsSettingsOpen(true);
  };

  const handleSettingsClose = () => {
    setIsSettingsOpen(false);
  };

  // Calculate summary statistics
  const summary = {
    wordCount: transcript.reduce((acc, curr) => acc + curr.text.split(' ').length, 0),
    speakers: [...new Set(transcript.map(t => t.speaker))].length,
    avgWords: transcript.length ? 
      transcript.reduce((acc, curr) => acc + curr.text.split(' ').length, 0) / 
      [...new Set(transcript.map(t => t.speaker))].length : 0,
  };

  const languageOptions = [
    { code: 'english', name: 'English', flag: 'üá∫üá∏' },
    { code: 'spanish', name: 'Spanish', flag: 'üá™üá∏' },
    { code: 'french', name: 'French', flag: 'üá´üá∑' },
    { code: 'german', name: 'German', flag: 'üá©üá™' },
    { code: 'chinese', name: 'Chinese', flag: 'üá®üá≥' },
  ];

  return (
    <div className="app">
      <div className="container">
        {/* Header */}
        <div className="glass-card header">
          <div className="header-content">
            <div className="header-left">
              <div className="logo">
                <span>üåê</span>
              </div>
              <div>
                <h1>Polyglot Meeting Whisperer</h1>
              </div>
            </div>
            <div className="header-time">
              {currentTime.toLocaleTimeString()}
            </div>
          </div>
        </div>

        {/* Control Buttons */}
        <ControlButtons
          isRecording={isRecording}
          isLoading={isLoading}
          onStartRecording={handleStartRecording}
          onStopRecording={handleStopRecording}
          onReset={handleReset}
          onExport={handleExport}
          onSettingsOpen={handleSettingsOpen}
        />

        {/* Dashboard */}
        <Dashboard 
          wordCount={summary.wordCount} 
          speakerCount={summary.speakers} 
          avgWords={summary.avgWords}
          sessionDuration={sessionDuration}
        />

        {/* Transcript Cards */}
        <div className="transcript-container">
          <TranscriptCard
            title="Original Transcript"
            icon="üìù"
            transcript={transcript}
            isLoading={isLoading}
            languageOptions={languageOptions}
            selectedLanguage={selectedOriginalLang}
            onLanguageChange={setSelectedOriginalLang}
            showAutoDetect={true}
          />
          
          <TranscriptCard
            title="Translated (EN)"
            icon="üåç"
            transcript={translatedTranscript}
            isLoading={isLoading}
            languageOptions={languageOptions}
            selectedLanguage={selectedTranslatedLang}
            onLanguageChange={setSelectedTranslatedLang}
            showAutoDetect={false}
          />
        </div>

        {/* Settings Modal */}
        <SettingsModal
          isOpen={isSettingsOpen}
          onClose={handleSettingsClose}
          language={language}
          setLanguage={setLanguage}
        />
      </div>
    </div>
  );
};

export default App;
